mapper.select:
SELECT TO_CHAR(res_time, 'HH24:MI') AS reserved_time
FROM reservation
WHERE TO_CHAR(res_time, 'YYYY-MM-DD') = #{date}
AND pro_idx = #{pro_idx}

SELECT res_idx, pat_idx, TO_CHAR(res_time, 'YYYY-MM-DD') AS res_time, pro_idx, res_chk, pay_chk 
FROM reservation
WHERE res_idx = #{res_idx}

mapper.update:
UPDATE reservation SET 
pro_idx = #{pro_idx},
res_time = TO_DATE(#{res_time}, 'YYYY-MM-DD HH24:MI:SS'),
res_chk = #{res_chk},
pay_chk = #{pay_chk}
WHERE res_idx = #{res_idx}
